import React, {useState} from 'react';
import ProductAdd from './ProductAdd';
import { Box, List, ListItem, ListItemButton, ListItemText } from '@mui/material';

export default function ProductList() {
  const [products, setProduct]= [
    {desc:"iPad", price:20000},
    {desc:"iPhone X", price:30000},
   ];

   const insert = function(newProduct){
     setProduct(oldProducts => [oldProducts, newProduct])
   }

   const [selectedIndex, setSelectedIndex] = useState(0);
   const handleListItemClick = (index) => {setSelectedIndex(index);};

  return (
    <Box sx={{
      width: '100vw',
      height: '100vh',
      backgroundColor: 'background.paper',
      color: 'black',
      textAlign: 'left'
    }}>
      <List subheader="Product list" aria-label="product list">
      {products.map((product, index) => 
        <ListItemButton
          selected={selectedIndex === index}
          onClick={() => handleListItemClick(index)}
          divider key={index}>
          <ListItemText primary={product.desc} secondary={"NT$"+product.price}></ListItemText>
        </ListItemButton>)}
      </List>
    </Box>
  );
}
